mapper.select:

select ORDER_ID, DELIVERY_SCORE, SERVICE_SCORE, CREATE_TIME, DEL_FLAG, MERCHANT_ID, VENDOR_ID
from rc_order_evaluation
where ORDER_ID = #{orderId,jdbcType=BIGINT}


    select ORDER_ID, DELIVERY_SCORE, SERVICE_SCORE, CREATE_TIME, DEL_FLAG, MERCHANT_ID, VENDOR_ID
    from rc_order_evaluation


        and DELIVERY_SCORE = #{deliveryScore,jdbcType=INTEGER}


        and SERVICE_SCORE = #{serviceScore,jdbcType=INTEGER}


        and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


        and CREATE_TIME >= #{createTimeFrom,jdbcType=TIMESTAMP}


        and CREATE_TIME <= #{createTimeTo,jdbcType=TIMESTAMP}


        and DEL_FLAG = #{delFlag,jdbcType=BIT}


        and MERCHANT_ID = #{merchantId,jdbcType=INTEGER}



	      MERCHANT_ID = #{mId,jdbcType=INTEGER}



        and VENDOR_ID = #{vendorId,jdbcType=INTEGER}



	  order by ${orderBy}


select ORDER_ID, DELIVERY_SCORE, SERVICE_SCORE, CREATE_TIME, DEL_FLAG, MERCHANT_ID, VENDOR_ID
from rc_order_evaluation


    and DELIVERY_SCORE = #{deliveryScore,jdbcType=INTEGER}


    and SERVICE_SCORE = #{serviceScore,jdbcType=INTEGER}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and DEL_FLAG = #{delFlag,jdbcType=BIT}


    and MERCHANT_ID = #{merchantId,jdbcType=INTEGER}


    and VENDOR_ID = #{vendorId,jdbcType=INTEGER}


limit 1

mapper.delete:

delete from rc_order_evaluation


    and DELIVERY_SCORE = #{deliveryScore,jdbcType=INTEGER}


    and SERVICE_SCORE = #{serviceScore,jdbcType=INTEGER}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and DEL_FLAG = #{delFlag,jdbcType=BIT}


    and MERCHANT_ID = #{merchantId,jdbcType=INTEGER}


    and VENDOR_ID = #{vendorId,jdbcType=INTEGER}
